import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddUser = () => {
  const [nombre, setnombre] = useState("");
  const [hora_entrada, sethora_entrada] = useState("");
  const [hora_salida, sethora_salida] = useState("Male");
  const navigate = useNavigate();

  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", {
        nombre,
        hora_entrada,
        hora_salida,
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={saveUser}>
          <div className="field">
            <label className="label">Nombre</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={nombre}
                onChange={(e) => setName(e.target.value)}
                placeholder="Nombre"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Hora Entrada</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={hora_entrada}
                onChange={(e) => sethora_entrada(e.target.value)}
                placeholder="Hora Entrada"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">hora_salida</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                  value={hora_salida}
                  onChange={(e) => setGender(e.target.value)}
                >
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </select>
              </div>
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddUser;
